mapper.select:
   SELECT EXISTS (SELECT * FROM record
   JOIN (
   SELECT "user".id as creator, partner.id as partner FROM "user" JOIN partner ON "user".partner_id = partner.id)
   AS cp ON record.creator_id = cp.creator
   JOIN personal_info_bl ON record.personal_id = personal_info_bl.id
   JOIN passport_info_bl pib ON record.passport_id = pib.id
   JOIN inn_bl ib on record.inn_id = ib.id
   JOIN phone_bl pb on record.phone_id = pb.id
   JOIN email_bl eb on record.email_id = eb.id
   WHERE partner = #{partnerId} AND ( 1=0

       OR surname = #{personalInfo.surname} AND first_name = #{personalInfo.firstName} AND
       second_name = #{personalInfo.secondName} AND birth_date = #{personalInfo.birthDate}


       OR pass_ser = #{passportInfo.passportSeria} AND pass_num = #{passportInfo.passportNumber}


       OR inn_num = #{inn}


       OR phone = #{phone}


       OR email = #{email}

   )
)

    SELECT EXISTS (SELECT * FROM record
    JOIN personal_info_bl ON record.personal_id = personal_info_bl.id
    JOIN passport_info_bl pib ON record.passport_id = pib.id
    JOIN inn_bl ib on record.inn_id = ib.id
    JOIN phone_bl pb on record.phone_id = pb.id
    JOIN email_bl eb on record.email_id = eb.id
    WHERE 1=0

        OR surname = #{personalInfo.surname} AND first_name = #{personalInfo.firstName}
               AND second_name = #{personalInfo.secondName} AND birth_date = #{personalInfo.birthDate}


         OR pass_ser = #{passportInfo.passportSeria} AND pass_num = #{passportInfo.passportNumber}


        OR inn_num = #{inn}


        OR phone = #{phone}


        OR email = #{email}

)
